import React from 'react'
import { Component } from 'react'
import { Container, Form, Button, Card } from 'react-bootstrap'
import { Editor } from '@tinymce/tinymce-react';

const { Configuration, OpenAIApi } = require("openai");

class AdsforGoogleSearch extends Component {
  constructor() {
    super()
    this.state = {
      heading: 'AI Response of:',
      response: 'AI Generated Results Here...'
    }

  }

  onFormSubmit = e => {
    //Start by Preventing the default
    e.preventDefault()

    const formData = new FormData(e.target),
      formDataObj = Object.fromEntries(formData.entries())
    console.log(formDataObj.productName)


    ////OPENAI///


    const configuration = new Configuration({
      apiKey: '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',
    });
    const openai = new OpenAIApi(configuration);

    openai.createCompletion("text-davinci-002", {
      prompt: `create ads for google search of the following product name: ${formDataObj.productName} and product description: ${formDataObj.productDescription}`,
      temperature: 0.8,
      max_tokens: 100,
      top_p: 1,
      frequency_penalty: 0,
      presence_penalty: 0,
    })
      .then((response) => {
        this.setState({
          heading: `AI response for: ${formDataObj.productName}`,
          response: `${response.data.choices[0].text}`
        })
      });


    //    this.setState({
    //      heading:`AI product Description Suggestion for:${formDataObj.productName }`,
    //      response: `The Respnse from OpenAI will be shown here`
    //    })
  }

  render() {

    return (
      <div>
        <Container>
          <br />
          <h1>Ads For Social Media</h1>
          <div className='container-fluid d-flex'>
            <div className='container'>

              <Form onSubmit={this.onFormSubmit}>
                <Form.Group className="mb-3" controlId="formBasicEmail">
                  <Form.Label>
                   Product Name
                  </Form.Label>
                  <Form.Control
                    type="text"
                    name="productName"
                    placeholder="Product Name" />
                  <Form.Text className="text-muted">
                    Enter as much information as possible for more acurate descriptions.
                  </Form.Text>
                </Form.Group>

                <Form.Group className="mb-3" controlId="formBasicEmail">
                  <Form.Label>
                   Product Description
                  </Form.Label>
                  <Form.Control
                    type="textarea"
                    name="productDescription"
                    placeholder="Describe your Product" />
                  <Form.Text className="text-muted">
                    Enter as much information as possible for more acurate descriptions.
                  </Form.Text>
                </Form.Group>

                <Button variant="dark" size="lg" type="submit">
                  Generate
                </Button>
              </Form>
            </div>

            <div className='container'>

              <Card>
                <Card.Body>
                  <Card.Title><h1> {this.state.heading} </h1></Card.Title>
                  <hr />
                  <Card.Text><p> {this.state.response} </p></Card.Text>
                </Card.Body>
              </Card>

            </div>

          </div>
          <div className='container'  style={{marginTop:'50px'}}>

            <Editor
              id="myEditor"
              apiKey="aomhk3dk1y1no3mai7xz9fbgd26lzimwz2p7m7bkcdr2kyn2"
              init={{
                menubar: false,
                branding: false,
                content_css: "/myStyle.css",
                selector: "textarea",
                plugins: [
                  "lists",
                  "image",
                  "paste",
                  "wordcount",
                  "tabfocus",
                   "spellchecker",
                ],
                setup: (editor) => {
                  this.editor = editor;
                },
                statusbar: false,
                resize: false,
                toolbar_sticky: true,
                paste_as_text: true,
                force_br_newlines: true,
                toolbar:
                  "undo redo | formatselect | " +
                  "bold italic backcolor | alignleft aligncenter " +
                  "alignright alignjustify | bullist numlist outdent indent | " +
                  "removeformat | help | wordcount ",
                  content_style: 'body { font-family:Helvetica,Arial,sans-serif; font-size:14px }'
              }}
            />
          </div>
        </Container>

      </div>
    )
  }
}
export default AdsforGoogleSearch
