import React from 'react'
import { Component,useState } from 'react'
import { Container, Form, Button, Card } from 'react-bootstrap'

const { Configuration, OpenAIApi } = require("openai");

class ProductDescription extends Component {
  constructor() {
    super()
    this.state = {
      heading: 'The Respnse from the AI will be shown here:',
      response: '..... await the response'
    }

  }

  onFormSubmit = e => {
    //Start by Preventing the default
    e.preventDefault()

    const formData = new FormData(e.target),
      formDataObj = Object.fromEntries(formData.entries())
    console.log(formDataObj.productName)


    ////OPENAI///


    const configuration = new Configuration({
      apiKey: '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',
    });
    const openai = new OpenAIApi(configuration);

    openai.createCompletion("text-davinci-002", {
      prompt: `Write a detailed, smart, informative and professional description for ${formDataObj.productName}`,
      temperature: 0.8,
      max_tokens: 100,
      top_p: 1,
      frequency_penalty: 0,
      presence_penalty: 0,
    })
      .then((response) => {
        this.setState({
          heading: `AI product description Suggestion for: ${formDataObj.productName}`,
          response: `${response.data.choices[0].text}`
        })
      });


    //    this.setState({
    //      heading:`AI product Description Suggestion for:${formDataObj.productName }`,
    //      response: `The Respnse from OpenAI will be shown here`
    //    })
  }

  render() {
    
    const [buttonText, setButtonText] = useState('Generate');
    function handleClick() {
      setButtonText('New text');
    }
    return (
      <div>
        <Container>
          <br />
          <br />
          <h1>Generate Product Description</h1>
          <br />

          <div className='container-fluid d-flex'>
            <div className='container'>

              <Form onSubmit={this.onFormSubmit}>
                <Form.Group className="mb-3" controlId="formBasicEmail">
                  <Form.Label>
                    What Product would you like to get a description for?
                  </Form.Label>
                  <Form.Control
                    type="text"
                    name="productName"
                    placeholder="Describe your Product" />
                  <Form.Text className="text-muted">
                    Enter as much information as possible for more acurate descriptions.
                  </Form.Text>
                </Form.Group>

                <Button variant="primary" size="lg" onClick={handleClick}  type="submit">
                {buttonText}
                </Button>
              </Form>
            </div>

            <div className='container'>

              <Card>
                <Card.Body>
                  <Card.Title><h1> {this.state.heading} </h1></Card.Title>
                  <hr />
                  <Card.Text><p> {this.state.response} </p></Card.Text>
                </Card.Body>
              </Card>

            </div>

          </div>
        </Container>
        <br />
        <br />
        <br />
        <br />
      </div>
    )
  }
}
export default ProductDescription
