import React from 'react'
import { Component } from 'react'
import { Container, Form, Button, Card} from 'react-bootstrap'

const { Configuration, OpenAIApi } = require("openai");

class ColdEmail extends Component {
  constructor()
  {
    super()
    this.state = {
      heading: 'The Respnse from the AI will be shown here:',
      response: '..... await the response', 
      email:'',
      email1:'',
    }

  }
  onFormSubmit = e =>{
    //Start by Preventing the default
    e.preventDefault()

    const formData = new FormData(e.target),
    formDataObj = Object.fromEntries(formData.entries())
    console.log(formDataObj.email)


    ////OPENAI///


    const configuration = new Configuration({
      apiKey: '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',
    });
    const openai = new OpenAIApi(configuration);

     openai.createCompletion("text-davinci-002", {
      prompt: ` ${formDataObj.email}`,
      temperature: 0.8,
      max_tokens: 250,
      top_p: 1,
      frequency_penalty: 0,
      presence_penalty: 0,
    })
    .then((response) => {
      this.setState({
        heading:`create an email where keypoints are: ${formDataObj.email}`,
        response: `${response.data.choices[0].text}`
      })
    });


//    this.setState({
//      heading:`AI product Description Suggestion for:${formDataObj.email }`,
//      response: `The Respnse from OpenAI will be shown here`
//    })
  }

  render() {
    return (
    <div>
      <Container>
        <br />
        <br />
        <h1>Generate Cold Email</h1>
        <br />
        <h4>Generate Cold Email for any type of the product, simple enter the name of product to get started.</h4>
        <br />
        <br />
        <Form onSubmit={this.onFormSubmit}>
              <Form.Group className="mb-3" controlId="formBasicEmail">
                  <Form.Label>
                      What Product would you like to get a description for?
                  </Form.Label>
                  <Form.Control
                          type="text"
                          name="email"
                          placeholder="Write an email to..." 
                          />
                  <Form.Text className="text-muted">
                    Enter as much information as possible for more acurate descriptions.
                  </Form.Text>
              </Form.Group>

              <Button variant="primary" size="lg" type="submit">
                  AI generator
              </Button>

        </Form>
        <br />
        <br />
        <Card>
            <Card.Body>
                <Card.Title><h1> {this.state.heading} </h1></Card.Title>
                <hr />
                <br />
                <Card.Text><h4> {this.state.response} </h4></Card.Text>
            </Card.Body>
          </Card>
      </Container>
      <br />
      <br />
      <br />
      <br />
    </div>
    )
  }
}
export default ColdEmail
