import React from 'react'
import { Component } from 'react'
import { Container, Form, Button, Card} from 'react-bootstrap'
const { Configuration, OpenAIApi } = require("openai");
import { Editor } from '@tinymce/tinymce-react';

class Tweets extends Component {
  constructor()
  {
    super()
    this.state = {
      heading: 'AI Tweet Suggestion for:',
      response: '..... await the response'
    }
  }
  onFormSubmit = e =>{
    //Start by Preventing the default
    e.preventDefault()

    const formData = new FormData(e.target),
    formDataObj = Object.formEntries(formData.entries())
    console.log(formDataObj.tweet)


    ////OPENAI///


    const configuration = new Configuration({
      apiKey: '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',
    });
    const openai = new OpenAIApi(configuration);

     openai.createCompletion("text-davinci-002", {
      prompt: `Write a detailed, smart, informative and professional tweet of ${formDataObj.tweet}`,
      temperature: 0.8,
      max_tokens: 100,
      top_p: 1,
      frequency_penalty: 0,
      presence_penalty: 0,
    })
    .then((response) => {
      this.setState({
        heading:`AI product description Suggestion for: ${formDataObj.tweet}`,
        response: `${response.data.choices[0].text}`
      })
    });


//    this.setState({
//      heading:`AI product Description Suggestion for:${formDataObj.tweet }`,
//      response: `The Respnse from OpenAI will be shown here`
//    })
  }

  render() {
    return (
    <div>
        
       <Container>
        <br />
        <br />
        <h1>Generate Tweets</h1>
        <br />
        <h4>Start generating tweets ideas with hashtags for your online social media compaigns on twitter. Create endless unique tweet ideas, no more writer block.</h4>
        <br />
        <br />
        <Form onSubmit={this.onFormSubmit}>
            <Form.Group className="mb-3" controlId="formBasicEmail">
              <Form.Label>What Product Would You like to tweet about?</Form.Label>
              <Form.Control
                      type="text"
                      name="tweet"
                      placeholder="Enter Product Name" />
              <Form.Text className="text-muted">
                Enter as much information as possible for more acurate descriptions.
              </Form.Text>
            </Form.Group>

            <Button variant="primary" size="lg" type="submit">
                AI Tweets generator
            </Button>

        </Form>
        <br />
        <br />
        <Card>
            <Card.Body>
                <Card.Title><h1> {this.state.heading} </h1></Card.Title>
                <hr />
                <br />
                <Card.Text><h4> {this.state.response} </h4></Card.Text>
            </Card.Body>
        </Card>
      </Container>
      <br />
      <br />
      <br />
      <br />
      </div>
    )
  }
}
export default Tweets
